import React, { useState } from "react";
import styled from "styled-components";

import scrollDown from "../../img/scrollDown.png";
import logo from "../../img/logo.jpg";
import expand from "../../img/expand.png";
import shrink from "../../img/shrink.png";

import NavBox from "./NavBox";
import About from "./About";
import Portfolio from "./Portfolio";
import Footer from "./Footer";

const NavbarToggler = styled.button`
  color: #e04343;
  background: rgba(0, 0, 0, 0);
  border: none;
`;

const Img = styled.img`
  width: 30px;
`;

const HomePage = () => {
  const [hover, setHover] = useState(false);
  const [collapsed, setCollapsed] = useState(true);
  const [collapsed2, setCollapsed2] = useState(true);

  const toggleNavbar = () => setCollapsed(!collapsed);
  const toggleNavbar2 = () => setCollapsed2(!collapsed2);

  return (
    <div>
      <header id="home">
        <div className="nav-box-cont">
          <NavbarToggler
            onClick={toggleNavbar}
            onMouseEnter={() => setHover(true)}
            onMouseOut={() => setHover(false)}
          >
            <Img
              src={collapsed || !hover ? expand : shrink}
              alt="expand icon"
            />
          </NavbarToggler>
          <NavbarToggler onClick={toggleNavbar2}>
            {collapsed2 ? <h2>Robbie who?</h2> : <h2>Ah okay!</h2>}
          </NavbarToggler>
        </div>
        <NavBox collapsed={collapsed} setCollapsed={setCollapsed} />
        <About collapsed2={collapsed2} setCollapsed2={setCollapsed2} />
        <div className="row">
          <div>
            <img src={logo} alt="logo" className="logo" />
            <p>a full-stack web developer</p>
            <a
              href="#portfolio-section"
              className={
                collapsed && collapsed2
                  ? "scrollto wow fadeInUp delay-5s"
                  : "scroll-hidden"
              }
            >
              <p className="scrollto-text">Portfolio</p>
              <p className="scrollto--arrow">
                <img src={scrollDown} alt="scroll down arrow" />
              </p>
            </a>
          </div>
        </div>
      </header>
      <Portfolio />
      <Footer />
    </div>
  );
};

export default HomePage;
