import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import styled from "styled-components";
import { Collapse, Navbar, Nav, NavItem, NavLink } from "reactstrap";

const NavbarToggler = styled.button`
  color: #e04343;
  background: rgba(0, 0, 0, 0);
  border: none;
`;

const Img = styled.img`
  width: 30px;
`;

const Icon = styled.img`
  width: 20px;
  display: none;
  transition: 0.3s ease-in-out;
`;

const Div = styled.div`
  color: #e7e9d1;
  :hover {
    color: #878b7b;
    transition: 0.3s ease-in-out;
  }
  &:hover ${Icon} {
    display: inline;
    transition: 0.3s ease-in-out;
  }
`;

const About = ({ collapsed2, setCollapsed2 }) => {
  const toggleNavbar = () => setCollapsed2(!collapsed2);

  return (
    <div>
      <Navbar color="faded" light>
        <Collapse isOpen={!collapsed2} navbar>
          <Nav navbar>
            <NavItem>
              <p>
                Full-stack web developer currently open to new opportunites.
              </p>
              <p>
                Skills: Javascript | HTML | CSS | React | Redux | RESTful APIs |
                Prismatopia | Node.js | Express | PostgreSQL
              </p>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

export default About;
