import React, { useState } from "react";

import scrollDown from "../../img/scrollDown.png";
import LOGO from "../../img/LOGO.png";
import logo_gif from "../../img/logo_gif.gif";
import logo from "../../img/logo.jpg";

import NavBox from "./NavBox";
import Portfolio from "./Portfolio";
import Footer from "./Footer";

const HomePage = () => {
  const [collapsed, setCollapsed] = useState(true);
  const [hover, setHover] = useState(false);
  return (
    <div>
      <header id="home">
        <NavBox collapsed={collapsed} setCollapsed={setCollapsed} />
        <div className="row">
          <div>
            <img
              onMouseEnter={() => setHover(true)}
              onMouseOut={() => setHover(false)}
              src={hover ? logo_gif : logo}
              alt="logo"
              className="logo"
            />
            <p>Full-stack web developer</p>
            <a
              href="#portfolio-section"
              className={
                collapsed ? "scrollto wow fadeInUp delay-5s" : "scroll-hidden"
              }
            >
              <p className="scrollto-text">Portfolio</p>
              <p className="scrollto--arrow">
                <img src={scrollDown} alt="scroll down arrow" />
              </p>
            </a>
          </div>
        </div>
      </header>
      <Portfolio />
      <Footer />
    </div>
  );
};

export default HomePage;
