import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import styled from "styled-components";
import expand from "../../img/expand.png";
import shrink from "../../img/shrink.png";
import portfolio from "../../img/portfolio.png";
import linkedin from "../../img/linkedin.png";
import github from "../../img/github.png";
import { Collapse, Navbar, Nav, NavItem, NavLink } from "reactstrap";

const NavbarToggler = styled.button`
  color: #e04343;
  background: rgba(0, 0, 0, 0);
  border: none;
`;

const Img = styled.img`
  width: 30px;
`;

const Icon = styled.img`
  width: 20px;
  display: none;
  transition: 0.3s ease-in-out;
`;

const Div = styled.div`
  color: white;
  &:hover ${Icon} {
    display: inline;
    transition: 0.3s ease-in-out;
  }
`;

const NavBox = ({ collapsed, setCollapsed }) => {
  const toggleNavbar = () => setCollapsed(!collapsed);

  return (
    <div>
      <Navbar color="faded" light>
        <NavbarToggler onClick={toggleNavbar}>
          <Img src={collapsed ? expand : shrink} alt="expand icon" />
        </NavbarToggler>
        <Collapse isOpen={!collapsed} navbar>
          <Nav navbar>
            <NavItem>
              <NavLink href="#portfolio-section" className="navEase">
                <Div>
                  <Icon src={portfolio} alt="portfolio icon" />
                  <> </> Portfolio <> </>
                  <Icon src={portfolio} alt="portfolio icon" />
                </Div>
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                target="_blank"
                href="https://www.linkedin.com/in/robbie-porter-85a10a82/"
                className="navEase"
              >
                <Div>
                  <Icon src={linkedin} alt="portfolio icon" />
                  <> </> LinkedIn <> </>
                  <Icon src={linkedin} alt="portfolio icon" />
                </Div>
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink
                target="_blank"
                href="https://github.com/Hammurobbie"
                className="navEase"
              >
                <Div>
                  <Icon src={github} alt="portfolio icon" />
                  <> </> Github <> </>
                  <Icon src={github} alt="portfolio icon" />
                </Div>
              </NavLink>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

export default NavBox;
